settings.threshold-text:
{%- if product.selected_or_first_available_variant.inventory_quantity <= section.settings.min-threshold -%}

<span>Low stock!</span>
<span>Only <strong>{{ product.selected_or_first_available_variant.inventory_quantity }}</strong> left in stock!</span>

       <progress id="progess_bar" value="25" max="100">25%</progress>
    {%- elsif product.selected_or_first_available_variant.inventory_quantity > section.settings.min-threshold and product.selected_or_first_available_variant.inventory_quantity <= section.settings.max-threshold -%}

<span>Limited Stock!</span>
<span>Only <strong>{{ product.selected_or_first_available_variant.inventory_quantity }}</strong> left in stock!</span>


      <progress id="progess_bar" value="50" max="100">50%</progress>
    {% else %}

<span>Available now!</span>
<span>Only <strong>{{ product.selected_or_first_available_variant.inventory_quantity }}</strong> left in stock!</span>


     <progress id="progess_bar" value="100" max="100">{{ product.selected_or_first_available_variant.inventory_quantity }}%</progress>
    {%- endif -%}
